import {PrintModel} from "../types/PrintModel";
import AsyncImage from './AsyncImage';
import styled from "styled-components";

const PrintModelComponent = (
    {
        product
    }: {
        product: PrintModel | null,
    }
) =>
    <Wrapper>
        <div className="card" style={{width: '300px', margin: 'auto'}}>
            <AsyncImage
                src={product?.preview}
                className="card-img-top"
                width={300}
                height={300}
                cachedImages={{}}
                bgColor={'hsl(0, 0%, 100%)'}
                preloadBgColor={'hsl(0, 0%, 75%)'}
            />
            <div className="card-body">
                <h5 className="card-title">{product?.modelName}</h5>
                <p className="card-text">{product?.modelName}</p>
                <p className="card-text">{product?.id}</p>
                <p className="card-text" style={{textDecoration: 'line-through'}}>{product?.id}</p>
                <p className="card-text">{product?.id}</p>
            </div>
        </div>;
    </Wrapper>


const Wrapper = styled.section`
    .container {
        padding: 9rem 0;
    }

    .product_images {
        display: flex;
        align-items: center;
    }

    .product-data {
        display: flex;
        flex-direction: column;
        align-items: flex-start;
        justify-content: center;
        gap: 2rem;

        .product-data-warranty {
            width: 100%;
            display: flex;
            justify-content: space-between;
            align-items: center;
            border-bottom: 1px solid #ccc;
            margin-bottom: 1rem;

            .product-warranty-data {
                text-align: center;

                .warranty-icon {
                    background-color: rgba(220, 220, 220, 0.5);
                    border-radius: 50%;
                    width: 4rem;
                    height: 4rem;
                    padding: 0.6rem;
                }

                p {
                    font-size: 1.4rem;
                    padding-top: 0.4rem;
                }
            }
        }

        .product-data-price {
            font-weight: bold;
        }

        .product-data-real-price {
            color: ${({theme}) => theme.colors.btn};
        }

        .product-data-info {
            display: flex;
            flex-direction: column;
            gap: 1rem;
            font-size: 1.8rem;

            span {
                font-weight: bold;
            }
        }

        hr {
            max-width: 100%;
            width: 90%;
            /* height: 0.2rem; */
            border: 0.1rem solid #000;
            color: red;
        }
    }

    .product-images {
        display: flex;
        justify-content: center;
        align-items: center;
    }

    .page_loading {
        font-size: 3.2rem;
        display: flex;
        justify-content: center;
        align-items: center;
    }

    @media (max-width: ${({theme}) => theme.media.mobile}) {
        padding: 0 2.4rem;
    }
`;

export default PrintModelComponent;
