import {Component} from "react";
import styled from "styled-components";
import {initialSingleState, initialState, SingleState, State} from "../types/state";
import {getCard} from "../services/product";
// import PrintModelCardComponent from "../components/PrintModelCardComponent";
// import PrintModelComponent from "../components/PrintModelComponent";
import AsyncImage from "../components/AsyncImage";
import PrintModelCardComponent from "../components/PrintModelCardComponent";
import PrintModelComponent from "../components/PrintModelComponent";

// interface RouteParams {
//     id: string;
// }

export class ModelPage extends Component<{ id: string }> {

    state: SingleState = initialSingleState;

    async componentDidMount() {
        try {
            const {id} = this.props; // Получаем id непосредственно из props
            const product = await getCard(id); // Используем id для вызова функции
            console.log('Product from getCard:', product);
            this.setState({product});
        } catch (error) {
            console.error('Error fetching product:', error);
        }
    }

    render() {
        const {id} = this.props;
        const {product} = this.state;
        console.log("ModelPage product - " + this.state)
        return (
            <Wrapper>
                <div>
                    <div>
                        <h1>Model Page</h1>
                        <p>Model ID: {id}</p>
                        <p>Model name: {product?.modelName}</p>
                        <p>Model name: {product?.preview}</p>
                    </div>
                    <div className="col my-3">
                        <PrintModelComponent product={product}/>
                    </div>
                </div>
            </Wrapper>
        );
    }
}


const Wrapper = styled.section`
    .container {
        padding: 9rem 0;
    }

    .product_images {
        display: flex;
        align-items: center;
    }

    .product-data {
        display: flex;
        flex-direction: column;
        align-items: flex-start;
        justify-content: center;
        gap: 2rem;

        .product-data-warranty {
            width: 100%;
            display: flex;
            justify-content: space-between;
            align-items: center;
            border-bottom: 1px solid #ccc;
            margin-bottom: 1rem;

            .product-warranty-data {
                text-align: center;

                .warranty-icon {
                    background-color: rgba(220, 220, 220, 0.5);
                    border-radius: 50%;
                    width: 4rem;
                    height: 4rem;
                    padding: 0.6rem;
                }

                p {
                    font-size: 1.4rem;
                    padding-top: 0.4rem;
                }
            }
        }

        .product-data-price {
            font-weight: bold;
        }

        .product-data-real-price {
            color: ${({theme}) => theme.colors.btn};
        }

        .product-data-info {
            display: flex;
            flex-direction: column;
            gap: 1rem;
            font-size: 1.8rem;

            span {
                font-weight: bold;
            }
        }

        hr {
            max-width: 100%;
            width: 90%;
            /* height: 0.2rem; */
            border: 0.1rem solid #000;
            color: red;
        }
    }

    .product-images {
        display: flex;
        justify-content: center;
        align-items: center;
    }

    .page_loading {
        font-size: 3.2rem;
        display: flex;
        justify-content: center;
        align-items: center;
    }

    @media (max-width: ${({theme}) => theme.media.mobile}) {
        padding: 0 2.4rem;
    }
`;
